//@version=5
indicator("Webhook EMA Crossover", overlay=true)

// Input parameters
fast_length = input.int(12, "Fast EMA Length")
slow_length = input.int(26, "Slow EMA Length")

// Calculate EMAs
ema_fast = ta.ema(close, fast_length)
ema_slow = ta.ema(close, slow_length)

// Detect crossovers
bullish_cross = ta.crossover(ema_fast, ema_slow)
bearish_cross = ta.crossunder(ema_fast, ema_slow)

// Plot EMAs
plot(ema_fast, color=color.green, linewidth=2, title="Fast EMA")
plot(ema_slow, color=color.red, linewidth=2, title="Slow EMA")

// Alert conditions
if bullish_cross
    alert('{"strategy": "EMA_Crossover", "symbol": "' + syminfo.ticker + '", "signal": "buy", "price": ' + str.tostring(close) + ', "stop_loss": ' + str.tostring(close * 0.98) + ', "take_profit": ' + str.tostring(close * 1.04) + '}', alert.freq_once_per_bar_close)

if bearish_cross
    alert('{"strategy": "EMA_Crossover", "symbol": "' + syminfo.ticker + '", "signal": "sell", "price": ' + str.tostring(close) + ', "stop_loss": ' + str.tostring(close * 1.02) + ', "take_profit": ' + str.tostring(close * 0.96) + '}', alert.freq_once_per_bar_close)
